mapper.select:
SELECT e.*
FROM biz_exam e
JOIN biz_paper p ON p.id = e.paper_id


    AND p.subject = #{subject}


ORDER BY e.start_time DESC, e.id DESC
LIMIT #{offset}, #{limit}

SELECT * FROM biz_exam


    AND status = #{status}


ORDER BY create_time DESC
LIMIT #{offset}, #{limit}

mapper.update:
UPDATE biz_exam
SET name = #{name}, paper_id = #{paperId}, start_time = #{startTime},
    end_time = #{endTime}, duration = #{duration}, status = #{status}
WHERE id = #{id}

mapper.select:
SELECT COUNT(DISTINCT er.student_id)
FROM biz_exam_record er
WHERE er.exam_id = #{examId}

SELECT COUNT(1) FROM biz_exam e
JOIN biz_exam_student es ON es.exam_id = e.id

  es.user_id = #{studentId}
  AND e.status = #{status}

SELECT e.* FROM biz_exam e
JOIN biz_exam_student es ON es.exam_id = e.id

  es.user_id = #{studentId}
  AND e.status = #{status}

ORDER BY e.start_time DESC, e.id DESC
LIMIT #{limit} OFFSET #{offset}

SELECT e.id AS id, e.name AS name, e.paper_id AS paperId, e.start_time AS startTime, e.end_time AS endTime, e.duration AS duration,
CASE WHEN NOW() < e.start_time THEN 'not_started' WHEN e.end_time IS NOT NULL AND NOW() > e.end_time THEN 'completed' WHEN e.end_time IS NULL AND e.duration IS NOT NULL AND NOW() > TIMESTAMPADD(MINUTE, e.duration, e.start_time) THEN 'completed' ELSE 'in_progress' END AS status, e.creator_id AS creatorId, e.create_time AS createTime, p.subject AS subject,
CASE WHEN MONTH(e.start_time) BETWEEN 3 AND 8 THEN CONCAT(YEAR(e.start_time), '_spring') ELSE CONCAT(YEAR(e.start_time), '_autumn') END AS semester, er.status AS recordStatus, er.score AS score, TIMESTAMPDIFF(SECOND, er.start_time, er.submit_time) AS timeTaken FROM biz_exam e JOIN biz_exam_student es ON es.exam_id = e.id LEFT JOIN biz_paper p ON p.id = e.paper_id LEFT JOIN biz_exam_record er ON er.exam_id = e.id AND er.student_id = es.user_id
es.user_id = #{studentId}
AND NOW() < e.start_time
AND e.start_time <= NOW() AND ( (e.end_time IS NOT NULL AND e.end_time >= NOW()) OR (e.end_time IS NULL AND e.duration IS NOT NULL AND TIMESTAMPADD(MINUTE, e.duration, e.start_time) >= NOW()) OR (e.end_time IS NULL AND e.duration IS NULL AND e.start_time <= NOW()) )
AND ( (e.end_time IS NOT NULL AND e.end_time < NOW()) OR (e.end_time IS NULL AND e.duration IS NOT NULL AND TIMESTAMPADD(MINUTE, e.duration, e.start_time) < NOW()) )
AND p.subject = #{subject}
AND ( CASE WHEN MONTH(e.start_time) BETWEEN 3 AND 8 THEN CONCAT(YEAR(e.start_time), '_spring') ELSE CONCAT(YEAR(e.start_time), '_autumn') END = #{semester} )
ORDER BY e.start_time DESC, e.id DESC LIMIT #{limit} OFFSET #{offset}
