mapper.select:
SELECT es.id AS id, u.id AS userId, u.username AS studentId, u.real_name AS name, COALESCE(c.class_name, '未分班') AS className, COALESCE(c.id, 0) AS classId, es.status AS status, es.add_time AS addTime FROM biz_exam_student es JOIN sys_user u ON u.id = es.user_id LEFT JOIN biz_class_student cs ON cs.user_id = u.id LEFT JOIN biz_class c ON c.id = cs.class_id WHERE es.exam_id = #{examId}
AND (u.username LIKE CONCAT('%', #{keyword}, '%') OR u.real_name LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY u.username LIMIT #{limit} OFFSET #{offset}

mapper.insert:
INSERT INTO biz_exam_student (exam_id, user_id, status) SELECT #{examId}, cs.user_id, 'normal' FROM biz_class_student cs WHERE cs.class_id IN
#{cid}
ON DUPLICATE KEY UPDATE status = 'normal'

mapper.delete:
DELETE FROM biz_exam_student 
WHERE exam_id = #{examId} 
AND user_id IN

  #{uid}
