mapper.select:
SELECT COUNT(1) FROM biz_exam_student es JOIN biz_exam e ON e.id = es.exam_id JOIN biz_paper p ON p.id = e.paper_id LEFT JOIN biz_subject s ON s.name COLLATE utf8mb4_unicode_ci = p.subject JOIN users u ON u.id = es.user_id LEFT JOIN biz_student bs ON bs.user_id = es.user_id LEFT JOIN ( SELECT tcs.user_id AS userId, tc.subject_id AS subjectId, MIN(tc.id) AS classId, MIN(tc.name) AS className FROM biz_teaching_class_student tcs JOIN biz_teaching_class tc ON tc.id = tcs.class_id GROUP BY tcs.user_id, tc.subject_id ) tcmap ON tcmap.userId = es.user_id AND tcmap.subjectId = s.id LEFT JOIN biz_exam_record er ON er.exam_id = es.exam_id AND er.student_id = es.user_id
AND es.exam_id = #{examId} AND tcmap.classId = #{classId}
AND ( u.username LIKE CONCAT('%', #{keyword}, '%') OR bs.student_no LIKE CONCAT('%', #{keyword}, '%') OR COALESCE(bs.real_name, u.real_name, u.username) LIKE CONCAT('%', #{keyword}, '%') )

SELECT er.id AS scoreId, u.id AS studentId, COALESCE(bs.student_no, u.username) AS studentNo, COALESCE(bs.real_name, u.real_name, u.username) AS name, tcmap.classId AS classId, tcmap.className AS className, er.score AS score, CASE WHEN er.status >= 2 AND er.score IS NOT NULL THEN 'graded' ELSE 'ungraded' END AS status, er.submit_time AS submitTime, er.submit_time AS gradingTime FROM biz_exam_student es JOIN biz_exam e ON e.id = es.exam_id JOIN biz_paper p ON p.id = e.paper_id LEFT JOIN biz_subject s ON s.name COLLATE utf8mb4_unicode_ci = p.subject JOIN users u ON u.id = es.user_id LEFT JOIN biz_student bs ON bs.user_id = es.user_id LEFT JOIN ( SELECT tcs.user_id AS userId, tc.subject_id AS subjectId, MIN(tc.id) AS classId, MIN(tc.name) AS className FROM biz_teaching_class_student tcs JOIN biz_teaching_class tc ON tc.id = tcs.class_id GROUP BY tcs.user_id, tc.subject_id ) tcmap ON tcmap.userId = es.user_id AND tcmap.subjectId = s.id LEFT JOIN biz_exam_record er ON er.exam_id = es.exam_id AND er.student_id = es.user_id
AND es.exam_id = #{examId} AND tcmap.classId = #{classId}
AND ( bs.student_no LIKE CONCAT('%', #{keyword}, '%') OR COALESCE(bs.real_name, u.real_name, u.username) LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY COALESCE(er.submit_time, '1970-01-01 00:00:00') DESC, u.id DESC LIMIT #{limit} OFFSET #{offset}

SELECT * FROM biz_exam_record
WHERE exam_id = #{examId} AND student_id = #{studentId}
LIMIT 1

SELECT * FROM biz_exam_answer
WHERE record_id = #{recordId}
ORDER BY id

mapper.update:
UPDATE biz_exam_record
SET score = #{score}
WHERE id = #{recordId}

UPDATE biz_exam_record
SET status = #{status}
WHERE id = #{recordId}

UPDATE biz_exam_record
SET status = #{status}
WHERE id IN

  #{id}
