mapper.update:
UPDATE biz_teaching_class
SET name = #{name},
    assigned_teacher_id = #{assignedTeacherId},
    academic_year = #{academicYear},
    semester = #{semester}
WHERE id = #{id}

mapper.delete:
DELETE FROM biz_teaching_class_student
WHERE class_id IN (
  SELECT id FROM biz_teaching_class WHERE subject_id = #{courseId}
)
